const out = [];
for (const item of items) {
  const m = item.json;
  out.push({
    json: {
      account_id: $json.threadId || "default",
      external_id: m.id,
      subject: m.payload?.headers?.find(h => h.name === "Subject")?.value || m.snippet,
      snippet: m.snippet,
      plain_text: m.payload?.parts?.find(p => p.mimeType === "text/plain")?.body?.data || m.snippet,
      ts: new Date(parseInt(m.internalDate)).toISOString(),
      tags: ["gmail"],
      source_url: `https://mail.google.com/mail/u/0/#inbox/${m.id}`,
      metadata: {
        threadId: m.threadId,
        labelIds: m.labelIds,
        from: m.payload?.headers?.find(h => h.name === "From")?.value,
        to: m.payload?.headers?.find(h => h.name === "To")?.value,
        cc: m.payload?.headers?.find(h => h.name === "Cc")?.value,
        messageId: m.payload?.headers?.find(h => h.name === "Message-Id")?.value
      },
      content_hash: null
    }
  });
}
return out;
